mapper.select:
select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", b.user_ids as "user_ids", b.user_names as "user_names" from system_group a left join ( select GROUP_CONCAT(user_id) as "user_ids",GROUP_CONCAT(user_name) as "user_names",group_id as "group_id" from system_user_group group by group_id ) b on a.id=b.group_id where 1=1
and a.name like concat('%',concat(#{obj.name},'%'))
and a.short_name like concat('%',concat(#{obj.short_name},'%'))
and a.type = #{obj.type}
and a.status = #{obj.status}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_group a where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.short_name like concat('%',concat(#{short_name},'%'))
and a.type = #{type}
and a.status = #{status}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.user_id as "user_id", a.group_id as "group_id", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "user_name" from system_user_group a left join system_user b on a.user_id = b.id where 1=1
and a.user_id = #{user_id}
and a.group_id = #{group_id}
order by a.create_time desc
