mapper.select:
select a.id as "id", a.type as "type", a.name as "name", a.path as "path", a.redirect as "redirect", a.component as "component", a.title as "title", a.keepAlive as "keepAlive", a.icon as "icon", a.permission as "permission", a.menu_cascade as "menu_cascade", a.parent_id as "parent_id", a.status as "status", a.level_num as "level_num", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", ifnull(b.num,0) as "child_num" from system_menu a left join ( select count(*) as "num", a.parent_id as "parent_id" from system_menu a group by a.parent_id ) b on a.id=b.parent_id where 1=1
and a.name like concat('%',concat(#{obj.name},'%'))
and a.menu_cascade like concat('%',concat(#{obj.menu_cascade},'%'))
and a.parent_id = #{obj.parent_id}
and a.status = #{obj.status}
and a.type = #{obj.type}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.name as "name", a.path as "path", a.redirect as "redirect", a.component as "component", a.title as "title", a.keepAlive as "keepAlive", a.icon as "qf_icon", a.permission as "permission", a.menu_cascade as "menu_cascade", a.parent_id as "parent_id", a.status as "status", a.level_num as "level_num", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", ifnull(b.num,0) as "child_num", c.role_ids as "role_ids" from system_menu a left join ( select count(*) as "num", a.parent_id as "parent_id" from system_menu a where 1=1
and a.type in
#{type}
group by a.parent_id ) b on a.id=b.parent_id left join ( select GROUP_CONCAT(a.role_id) as "role_ids", a.menu_id as "menu_id" from system_role_menu a group by a.menu_id ) c on a.id=c.menu_id where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.menu_cascade like concat('%',concat(#{menu_cascade},'%'))
and a.parent_id = #{parent_id}
and a.status = #{status}
and a.type = #{type}
and a.type in
#{type}
order by a.order_by+0 asc , a.create_time desc
