mapper.select:
select a.table_name as "key", a.table_name as "table_name", a.table_comment as "table_comment", a.create_time as "create_time", a.update_time as "update_time" from information_schema.tables a where a.table_schema=#{table_schema} and a.table_type='BASE TABLE' and a.table_name not like 'qrtz_%' and a.table_name not like 'system_%' and a.table_name not like 'act_%' and a.table_name not like 'zipkin_%' and a.table_name not in ( select table_name from system_gentable )
and a.table_name like concat('%',concat(#{table_name},'%'))
and a.table_comment like concat('%',concat(#{table_comment},'%'))
and a.create_time >= #{start_time}
and a.create_time <= #{end_time}
limit #{start_page},#{pageSize}

 select
count(*)
 from information_schema.tables a
 where a.table_schema=#{table_schema}
 and a.table_type='BASE TABLE' and
 a.table_name not like 'qrtz_%' and a.table_name not like 'system_%' and a.table_name not like 'act_%' and a.table_name not like 'zipkin_%'
 and a.table_name not in (
 select table_name from system_gentable
 )

     and a.table_name like concat('%',concat(#{table_name},'%'))


     and a.table_comment like concat('%',concat(#{table_comment},'%'))


     and a.create_time >= #{start_time}


     and a.create_time  <=  #{end_time}

select a.id as "id", a.type as "type", a.table_name as "table_name", a.table_comment as "table_comment", a.temp_type as "temp_type", a.pack_path as "pack_path", a.mod_name as "mod_name", a.bus_name as "bus_name", a.menu_name as "menu_name", a.menu_id as "menu_id", a.gen_type as "gen_type", a.gen_path as "gen_path", a.tree_id as "tree_id", a.tree_pid as "tree_pid", a.tree_name as "tree_name", a.order_by as "order_by", a.open_process as "open_process", a.process_key as "process_key", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", b.name as "menu_pname" from system_gentable a left join system_menu b on a.menu_id=b.id where 1=1
and a.table_name like concat('%',concat(#{obj.table_name},'%'))
and a.table_comment like concat('%',concat(#{obj.table_comment},'%'))
and a.create_time >= #{obj.start_time}
and a.create_time <= #{obj.end_time}
and a.temp_type = #{obj.temp_type}
and a.mod_name like concat('%',concat(#{obj.mod_name},'%'))
and a.bus_name like concat('%',concat(#{obj.bus_name},'%'))
order by a.order_by+0 asc , a.create_time desc
